mapper.update:
    update study_share
	   set title=#{title}, content=#{content}
	 where sha_bno=#{shaBno}

update study_share
set view_cnt=view_cnt+1
where sha_bno = #{shaBno}

update study_share
set reco_num=reco_num+1
where sha_bno = #{shaBno}

update study_share
set report_cnt=report_cnt+1
where sha_bno = #{shaBno}

mapper.select:
select report_cnt
from study_share
where sha_bno = #{shaBno}

mapper.update:
update study_share
set available = 0
where sha_bno = #{shaBno}
